import { InputLabel, MenuItem, Select } from "@mui/material";
import { RedButton } from "../../../components/Button/Button";
import InfoTable from "../../../components/Table/Table";
import { LogisticSelector, SellerCol, SellerRow } from "./types";
import { useState } from "react";

const SellerHome = () => {
  const [age, setAge] = useState("");
  const [rowStates, setRowStates] = useState(
    rows.map((row, index) => ({
      ...row,
      logisticValue: 10, // Í∏∞Î≥∏Í∞í ÏÑ§Ï†ï
      id: `row-${index}`, // ÏãùÎ≥ÑÏûê Ï∂îÍ∞Ä
    }))
  );
  const handleLogisticChange = (
    event: React.ChangeEvent<{ value: unknown }>, // Select Ïª¥Ìè¨ÎÑåÌä∏Ïùò Ïù¥Î≤§Ìä∏ ÌÉÄÏûÖ
    id: string // LogisticSelectorÏóê Ï†ÑÎã¨Îêú Í≥†Ïú† ID
  ) => {
    setRowStates((prev) =>
      prev.map(
        (row) =>
          row.id === id
            ? { ...row, logisticValue: event.target.value as number } // Í∞í ÏóÖÎç∞Ïù¥Ìä∏
            : row // Îã§Î•∏ ÌñâÏùÄ Í∑∏ÎåÄÎ°ú Ïú†ÏßÄ
      )
    );
  };

  const cols: SellerCol[] = [
    { id: "img", label: "Ïù¥ÎØ∏ÏßÄ", minWidth: 100 },
    { id: "name", label: "ÏÉÅÌíàÎ™Ö", minWidth: 100 },
    { id: "price", label: "Í∞ÄÍ≤©", minWidth: 100 },
    { id: "customerName", label: "Íµ¨Îß§Ïûê Ïù¥Î¶Ñ", minWidth: 100 },
    { id: "customerPhone", label: "Íµ¨Îß§Ïûê Ï†ÑÌôîÎ≤àÌò∏", minWidth: 120 },
    { id: "customerAddress", label: "Íµ¨Îß§Ïûê Ï£ºÏÜå", minWidth: 120 },
    { id: "logistic", label: "Ïö¥ÏÜ°ÌöåÏÇ¨", minWidth: 100 },
  ];

  const logisticSelector = ({ id }: LogisticSelector) => {
    return (
      <div className="w-full">
        <Select
          labelId="demo-simple-select-label"
          id={`select-${id}`}
          value={age}
          label="Age"
          onChange={handleLogisticChange}
          className="w-full"
        >
          <MenuItem value={10}>logistics_A</MenuItem>
        </Select>
      </div>
    );
  };

  const rows: SellerRow[] = [
    {
      img: "üçî",
      name: "ÏÉÅÌíàA",
      price: 3000,
      customerName: "customerA",
      customerPhone: "010-1234-5678",
      customerAddress: "ÏÑúÏö∏Ïãú Í∞ïÎÇ®Íµ¨",
      logistic: null,
    },
    {
      img: "üçî",
      name: "ÏÉÅÌíàA",
      price: 3000,
      customerName: "customerB",
      customerPhone: "010-1234-5678",
      customerAddress: "Î∂ÄÏÇ∞Ïãú Í∏àÏ†ïÍµ¨",
      logistic: null,
    },
    {
      img: "üíº",
      name: "ÏÉÅÌíàD",
      price: 3000,
      customerName: "customerB",
      customerPhone: "010-1234-5678",
      customerAddress: "Î∂ÄÏÇ∞Ïãú Í∏àÏ†ïÍµ¨",
      logistic: logisticSelector(),
    },
  ];

  return (
    <div className="flex flex-col w-full px-16 py-8 gap-4">
      <div className="flex flex-col gap-2 mb-4">
        <div className="text-2xl font-bold">Î¨ºÌíàÍµ¨Îß§</div>
        <div className="text-sm text-gray-500">
          Î¨ºÌíàÏùÄ ÌïúÎ≤àÏóê ÌïòÎÇòÎßå Íµ¨Îß§ Í∞ÄÎä•Ìï©ÎãàÎã§. Íµ¨Îß§ÌïòÍ≥†Ïûê ÌïòÎäî ÏÉÅÌíàÏùÑ ÏÑ†ÌÉùÌïú ÌõÑ
          Î¨ºÌíà Íµ¨Îß§ Î≤ÑÌäºÏùÑ ÎàåÎü¨Ï£ºÏÑ∏Ïöî
        </div>
      </div>
      <InfoTable cols={cols} rows={rows} />
      <RedButton buttonText="Î¨ºÎ•ò ÌöåÏÇ¨Î°ú Ï†ïÎ≥¥ Ï†ÑÏÜ° >>" />
    </div>
  );
};
export default SellerHome;
