import { Button } from "@mui/material";
import deliverLogoDBG from "../assets/deliverLogo_DBG.png";

const SideBar = () => {
  const customerSideOptions = [
    {
      name: "주문목록",
      link: "/customer/home",
    },
    {
      name: "물품조회",
      link: "/customer/order",
    },
    {
      name: "배송상태조회",
      link: "/customer/orderState",
    },
  ];

  return (
    <div class="bg-backgroundBlue w-1/5 h-screen flex flex-col">
      <div>
        <div class="bg-backgroundBlue w-full  flex flex-col items-center py-8 gap-4">
          <img src={deliverLogoDBG} alt="logo" class="w-32" />
          <div class="font-bold text-blueLight">CUSTOMER1</div>

          {customerSideOptions.map((option) => (
            <div class="bg-white px-2 py-2 rounded-md w-4/5 text-sm font-bold text-blue hover:text-white hover:bg-blueLight active:text-white active:bg-blueLight">
              <a href={option.link}>{option.name}</a>
            </div>
          ))}
        </div>
      </div>
      <div class="bg-white px-2 py-2 rounded-md w-4/5 text-sm font-bold text-blue hover:text-white hover:bg-blueLight active:text-white active:bg-blueLight">
        <a href={"/login"}>로그아웃</a>
      </div>
    </div>
  );
};
export default SideBar;
